// GUI Command Dispatching Pattern
@Observable
class AppState {
    private let spoofingManager: IdentitySpoofingManager
    var isProcessing: Bool = false
    var lastError: Error?

    // Command dispatching with async support
    func enableSpoofing() async {
        isProcessing = true
        lastError = nil

        do {
            await spoofingManager.enable()
        } catch {
            lastError = error
        }

        isProcessing = false
    }

    // Dispatching with parameters
    func updateConfiguration(_ profile: ConfigurationProfile) async {
        await configManager.setProfile(profile)
    }
}

// SwiftUI View dispatching commands
struct ControlPanel: View {
    let appState: AppState

    var body: some View {
        Button("Enable Spoofing") {
            Task {
                await appState.enableSpoofing()
            }
        }
        .disabled(appState.isProcessing)
    }
}
