// AppState integration
class AppState: ObservableObject {
    let errorManager: ErrorManager = ErrorManager.shared

    func handleError(_ error: Error, context: String) {
        errorManager.handleError(error, context: context)
    }
}

// SwiftUI View integration
struct ContentView: View {
    @EnvironmentObject var appState: AppState

    var body: some View {
        NavigationView { ... }
            .withErrorHandling(errorManager: appState.errorManager)
    }
}

// Business logic error handling
func toggleModule(_ moduleId: String) async {
    guard let module = findModule(moduleId) else {
        let error = PrivarionError.moduleNotFound(moduleId: moduleId)
        handleError(error, context: "ModuleToggle", operation: "toggle")
        return
    }

    do {
        try await moduleInteractor.toggleModule(module)
    } catch {
        let privarionError = PrivarionError.moduleToggleFailed(
            moduleId: moduleId, 
            reason: error.localizedDescription
        )
        handleError(privarionError, context: "ModuleToggle", operation: "toggle")
    }
}

// Custom recovery implementation
func retryWithCustomLogic(errorId: String) async {
    await errorManager.retryOperation(errorId: errorId) {
        // Custom retry logic here
        try await performNetworkOperation()
    }
}
